import { EMPTY } from 'rxjs';
import { switchMap } from 'rxjs/operators';
import { DeleteDialogComponent } from './delete-dialog/delete-dialog.component';
export const openDialog = (dialogRef, andThen) => dialogRef.afterClosed().pipe(switchMap(x => x !== undefined ? andThen(x) : EMPTY));
export const openDeleteDialog = (dialog, data, ok) => openDialog(DeleteDialogComponent.instance(dialog, data), res => {
  switch (res) {
    case 'ok':
      return ok();
    case 'cancel':
      return EMPTY;
  }
});
