export const groupBy = (xs, key) => {
  const dict = new Map();
  xs.forEach(x => {
    const k = key(x);
    const collection = dict.get(k);
    if (!collection) {
      dict.set(k, [x]);
    } else {
      collection.push(x);
    }
  });
  return dict;
};
export const mapGroup = (map, f) => {
  const xs = [];
  forEachGroup(map, (k, v) => xs.push(f(k, v)));
  return xs;
};
export const forEachGroup = (map, f) => {
  let i = 0;
  map.forEach((v, k) => f(k, v, i++));
};
